import logo from './img/logo.png';
import textlogo from './img/text.png';
import folder2 from './img/folder2.png';
import './App.css';
import React, {useState, useEffect, useRef} from 'react';
import { cleanup } from '@testing-library/react';


function App() {

  const [date, setDate] = useState(new Date());

  const boxRef = useRef(null);
  const containerRef = useRef(null);
  const isClicked = useRef(false);
  const coords = useRef({startX: 0, startY: 0, lastX: 0, lastY: 0});

  useEffect(() => {
    const interval = setInterval(() => setDate(new Date()), 6000);
    if (!boxRef.current || !containerRef.current) {
      return () => clearInterval(interval);
    }
    const box = boxRef.current;
    const container = containerRef.current;

    const onMouseDown = (e) => {
      isClicked.current = true;
      coords.current.startX = e.clientX;
      coords.current.startY = e.clientY;
    }

    const onMouseUp = (e) => {
      isClicked.current = false;
      coords.current.lastX = box.offsetLeft;
      coords.current.lastY = box.offsetTop;
    }

    const onMouseMove = (e) => {
      if (!isClicked.current) {
        return ;
      }
      const nextX = e.clientX - coords.current.startX;
      const nextY = e.clientY - coords.current.startY;
      box.style.top = `${nextY}px`;
      box.style.left = `${nextX}px`;
    }

    box.addEventListener('mousedown', onMouseDown)
    box.addEventListener('mouseup', onMouseUp)
    container.addEventListener('mousemove', onMouseMove)

    const cleanUp = () => {
      box.removeEventListener('mousedown', onMouseDown);
      box.removeEventListener('mouseup', onMouseUp);
      container.removeEventListener('mousemove', onMouseMove)
    }
    return cleanUp;
  }, []);


