import logo from './img/logo.png';
import textlogo from './img/text.png';
import folder2 from './img/folder2.png';
import './App.css';
import React, {useState, useEffect, useRef} from 'react';
import { cleanup } from '@testing-library/react';

// ES6
import { Resizable } from 'react-resizable';

// ...
class Example extends React.Component {
  state = {
    width: 200,
    height: 200,
  };

  // On top layout
  onResize = (event, {element, size, handle}) => {
    this.setState({width: size.width, height: size.height});
  };

  render() {
    return (
      <Resizable height={this.state.height} width={this.state.width} onResize={this.onResize}>
        <div className="box" style={{width: this.state.width + 'px', height: this.state.height + 'px'}}>
          <span>Contents</span>
        </div>
      </Resizable>
    );
  }
}


function App() {

  const [date, setDate] = useState(new Date());

  const aboutRef = useRef(null);
  const aboutIsClicked = useRef(false);
  const aboutCoords = useRef({startX: 40, startY: 100, lastX: 40, lastY: 100});
  let aboutX = 40;
  let aboutY = 100;

  const projectsRef = useRef(null);
  const projectsIsClicked = useRef(false);
  const projectsCoords = useRef({startX: 100, startY: 200, lastX: 100, lastY: 200});
  let projectsX = 100;
  let projectsY = 200;

  const experienceRef = useRef(null);
  const experienceIsClicked = useRef(false);
  const experienceCoords = useRef({startX: 60, startY: 350, lastX: 60, lastY: 350});
  let experienceX = 60;
  let experienceY = 350;

  const containerRef = useRef(null);

  const refs =[
    {ref: aboutRef, isClicked: aboutIsClicked, coords: aboutCoords, x: aboutX, y: aboutY},
    {ref: projectsRef, isClicked: projectsIsClicked, coords: projectsCoords, x: projectsX, y: projectsY},
    {ref: experienceRef, isClicked: experienceIsClicked, coords: experienceCoords, x: experienceX, y: experienceY}
  ]

  const [renderAbout, setRenderAbout] = useState(true);

  useEffect(() => {
    const interval = setInterval(() => setDate(new Date()), 6000);

    for (let r of refs) {
      let [ref, isClicked, coords, x, y] = [r.ref, r.isClicked, r.coords, r.x, r.y];
      if (ref.current && containerRef.current) {
        const curr = ref.current;
        const container = containerRef.current;

        const onMouseDown = (e) => {
          isClicked.current = true;
          coords.current.startX = e.clientX;
          coords.current.startY = e.clientY;
          x = e.clientY;
          y = e.clientX;
        }

        const onMouseUp = (e) => {
          isClicked.current = false;
          coords.current.lastX = curr.offsetLeft;
          coords.current.lastY = curr.offsetTop;
          if (e.clientX == x && e.clientY == y) {
            console.log("yes");
          }
        }

        const onMouseMove = (e) => {
          if (!isClicked.current) {
            return ;
          }
          const nextX = e.clientX - coords.current.startX + coords.current.lastX;
          const nextY = e.clientY - coords.current.startY + coords.current.lastY;
          curr.style.top = `${nextY}px`;
          curr.style.left = `${nextX}px`;
        }

        r.down = onMouseDown;
        r.up = onMouseUp;
        r.move = onMouseMove;

        curr.addEventListener('mousedown', onMouseDown)
        curr.addEventListener('mouseup', onMouseUp)
        container.addEventListener('mousemove', onMouseMove)
        container.addEventListener('mouseleave', onMouseUp)
      }
    }

    const cleanUp = () => {
      for (let r of refs) {
        const curr = r.ref.current;
        const container = containerRef.current;
          curr.removeEventListener('mousedown', r.down);
          curr.removeEventListener('mouseup', r.up);
          container.removeEventListener('mousemove', r.move)
          container.removeEventListener('mouseleave', r.up)
      }
    }

    return () => {
      clearInterval(interval);
      cleanUp();
    }
  }, []);


    return (
      <div className="parent">
        <div className="entry">
          <div className="bar">
            <div className="logodiv">
              <img className="logo" src={logo}/>
              <div className="link">Email</div>
              <div className="link">LinkedIn</div>
              <div className="link">Github</div>
            </div>
            <div>
              <span className="date">{date.getMonth()+1}/{date.getDate()}/{date.getYear()+1900}</span>
              <span className="time">
              {date.getHours() > 12 ? date.getHours() - 12 : (date.getHours() == 0 ? 12 : date.getHours())}:{String(date.getMinutes()).length < 2 ? `0${String(date.getMinutes())}` : String(date.getMinutes())} {date.getHours() > 12 ? "PM" : "AM"}
              </span>
            </div>
          </div>
          <div className="main">
            <div className="container" ref={containerRef}>
            <div className="about" ref={aboutRef}>
              <div className="text-div">
                <img className="text" src={textlogo}/>
              </div>
              <div className="name">ABOUTME.txt</div>
            </div>
            <div className="projects" ref={projectsRef}>
              <div className="text-div">
                <img className="folder" src={folder2}/>
              </div>
              <div className="name">projects</div>
            </div>
            <div className="experience" ref={experienceRef}>
              <div className="text-div">
                <img className="folder" src={folder2}/>
              </div>
              <div className="name">experience</div>
            </div>
            {renderAbout && <div className="aboutwindow">
              <div className="windowbar"></div>
            </div>}

            </div>
          </div>
        </div>
      </div>
    );
}

export default App;
